import React, { useState } from "react";
import "./Input.css";

const Input = ({ type, placeholder, name, id }) => {
  const [numero, setNumero1] = useState();
  return (
    <>
      <input
        type={type}
        placeholder={placeholder}
        name={name}
        id={id}
        onChange={(e) => {setNumero1(e.target.value)}}
      />
      <span>{numero1}</span>
    </>
  );
};

export default Input;
